import React, { useState } from 'react';
import axios from 'axios';

function App() {
    const [file, setFile] = useState(null); // Armazena o arquivo CSV
    const [resultados, setResultados] = useState(null); // Armazena os resultados da API
    const [error, setError] = useState(null); // Armazena possíveis erros
  
    // Função para capturar o arquivo selecionado
    const handleFileChange = (event) => {
      setFile(event.target.files[0]);
      setResultados(null);  // Limpa os resultados anteriores
      setError(null);       // Limpa os erros anteriores
    };
  
    // Função para fazer upload do CSV e obter os resultados
    const handleFileUpload = async () => {
      if (!file) {
        alert("Por favor, selecione um arquivo.");
        return;
      }
  
      const formData = new FormData();
      formData.append("file", file);
  
      try {
        const response = await fetch("http://localhost:8000/upload_csv/", {
          method: "POST",
          body: formData,
        });
  
        // Verifica e exibe a resposta da API no console
        console.log("Resposta da API:", response);
  
        if (!response.ok) {
          throw new Error("Erro ao enviar o arquivo");
        }
  
        const data = await response.json();
        console.log("Dados recebidos:", data);  // Verifica se os dados estão corretos
  
        setResultados(data.resultados); // Armazena os resultados da API
        setError(null);  // Limpa o estado de erro
      } catch (error) {
        console.error("Erro ao fazer upload do arquivo:", error);
        setError("Ocorreu um erro ao processar o arquivo.");
        setResultados(null);  // Limpa os resultados anteriores
      }
    };
  
    return (
      <div className="App">
        <h1>Calculadora de Distância entre CEPs</h1>
        <input type="file" onChange={handleFileChange} />
        <button onClick={handleFileUpload}>Enviar CSV</button>
  
        {/* Exibe os resultados se houver */}
        {resultados && (
          <div>
            <h2>Resultados:</h2>
            <ul>
              {resultados.map((resultado, index) => (
                <li key={index}>
                  {resultado.CEP_A} - {resultado.CEP_B}: {resultado.Distancia_km ? `${resultado.Distancia_km} km` : resultado.Erro}
                </li>
              ))}
            </ul>
          </div>
        )}
  
        {/* Exibe mensagem de erro, se houver */}
        {error && (
          <div style={{ color: "red" }}>
            <p>{error}</p>
          </div>
        )}
      </div>
    );
  }
  
  export default App;
