import React, { useState } from 'react';
import axios from 'axios';

function App() {
    const [file, setFile] = useState(null);
    const [resultados, setResultados] = useState(null);

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleFileUpload = async () => {
        if (!file) {
            alert("Por favor, selecione um arquivo.");
            return;
        }

        const formData = new FormData();
        formData.append("file", file);

        try {
            const response = await fetch("http://localhost:8000/upload_csv/", {
                method: "POST",
                body: formData,
            });

            if (!response.ok) {
                throw new Error("Erro ao enviar o arquivo");
            }

            const data = await response.json();
            setResultados(data.resultados); // Exibe os resultados retornados
        } catch (error) {
            console.error("Erro ao fazer upload do arquivo:", error);
        }
    };

    return (
        <div className="App">
            <h1>Calculadora de Dist√¢ncia entre CEPs</h1>
            <input type="file" onChange={handleFileChange} />
            <button onClick={handleFileUpload}>Enviar CSV</button>

            {resultados && (
                <div>
                    <h2>Resultados:</h2>
                    <ul>
                        {resultados.map((resultado, index) => (
                            <li key={index}>
                                {resultado.CEP_A} - {resultado.CEP_B}: {resultado.Distancia_km || resultado.Erro} km
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
}

export default App;
