import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [distances, setDistances] = useState([]);

  const handleFileUpload = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:8000/upload-csv/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setDistances(response.data);
    } catch (error) {
      console.error('Erro ao fazer upload do CSV', error);
    }
  };

  return (
    <div className="App">
      <h1>Calculadora de Distância entre CEPs</h1>
      <form onSubmit={handleSubmit}>
        <input type="file" accept=".csv" onChange={handleFileUpload} />
        <button type="submit">Calcular Distâncias</button>
      </form>

      {distances.length > 0 && (
        <div>
          <h2>Resultados</h2>
          <table>
            <thead>
              <tr>
                <th>CEP A</th>
                <th>CEP B</th>
                <th>Distância (km)</th>
              </tr>
            </thead>
            <tbody>
              {distances.map((distance, index) => (
                <tr key={index}>
                  <td>{distance.CEP_A}</td>
                  <td>{distance.CEP_B}</td>
                  <td>{distance.Distancia_km ?? 'Indisponível'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
