import React, { useState } from 'react';

function App() {
  const [file, setFile] = useState(null); // Armazena o arquivo CSV selecionado
  const [resultados, setResultados] = useState(null); // Armazena os resultados retornados pela API
  const [error, setError] = useState(null); // Armazena possíveis erros

  // Captura o arquivo CSV selecionado
  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
    setResultados(null);  // Limpa os resultados anteriores
    setError(null);       // Limpa os erros anteriores
  };

  // Função que faz o upload do CSV e busca os resultados do backend
  const handleFileUpload = async () => {
    if (!file) {
      alert("Por favor, selecione um arquivo.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await fetch("http://localhost:8000/upload_csv/", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error("Erro ao enviar o arquivo.");
      }

      const data = await response.json();
      console.log("Dados recebidos:", data); // Verifica os dados no console
      setResultados(data.resultados); // Atualiza os resultados na página
      setError(null);  // Limpa o erro, caso exista
    } catch (error) {
      console.error("Erro ao fazer upload do arquivo:", error);
      setError("Ocorreu um erro ao processar o arquivo.");  // Mostra o erro para o usuário
      setResultados(null);  // Limpa os resultados anteriores
    }
  };

  return (
    <div className="App">
      <h1>Calculadora de Distância entre CEPs</h1>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleFileUpload}>Enviar CSV</button>

      {/* Exibe os resultados se existirem */}
      {resultados && (
        <div>
          <h2>Resultados:</h2>
          <ul>
            {resultados.map((resultado, index) => (
              <li key={index}>
                {resultado.CEP_A} - {resultado.CEP_B}: {resultado.Distancia_km ? `${resultado.Distancia_km} km` : resultado.Erro}
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Exibe mensagem de erro, se houver */}
      {error && (
        <div style={{ color: "red" }}>
          <p>{error}</p>
        </div>
      )}
    </div>
  );
}

export default App;
