import React, { useState } from 'react';
import './index.css';

function App() {
  const [resultados, setResultados] = useState([]);
  const [error, setError] = useState('');

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:8000/upload_csv/', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      if (response.ok) {
        setResultados(data.resultados);
        setError('');
      } else {
        setError('Ocorreu um erro ao processar o arquivo.');
      }
    } catch (error) {
      setError('Erro ao conectar com o servidor.');
    }
  };

  return (
    <div className="bg-gradient-to-r from-gray-100 via-gray-300 to-gray-500 min-h-screen p-6 flex flex-col items-center">
      <h1 className="text-4xl font-bold text-center text-gray-900 mb-6">
        Calculadora de Distância entre CEPs
      </h1>

      <div className="w-full max-w-md p-6 bg-white rounded-lg shadow-md">
        <input
          type="file"
          onChange={handleFileUpload}
          className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-500 file:text-white hover:file:bg-blue-600"
        />
      </div>

      {error && (
        <div className="text-red-500 mt-4">
          {error}
        </div>
      )}

      {resultados.length > 0 && (
        <div className="mt-8 w-full max-w-2xl bg-white p-6 rounded-lg shadow-lg">
          <h2 className="text-2xl font-semibold mb-4">Resultados:</h2>
          <table className="table-auto w-full">
            <thead>
              <tr>
                <th className="px-4 py-2">CEP A</th>
                <th className="px-4 py-2">CEP B</th>
                <th className="px-4 py-2">Distância (km)</th>
              </tr>
            </thead>
            <tbody>
              {resultados.map((resultado, index) => (
                <tr key={index}>
                  <td className="border px-4 py-2">{resultado.CEP_A}</td>
                  <td className="border px-4 py-2">{resultado.CEP_B}</td>
                  <td className="border px-4 py-2">
                    {resultado.Distancia_km ? `${resultado.Distancia_km} km` : resultado.Erro}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
